o4g5z46.css:
inline-size: 100%;
  max-inline-size: 100%;
  /* Begin Content Grid */
  :where(&) {
    --feature-width: 50px;
    --feature-max-width: 100px;
    --full: minmax(var(--gutter), 1fr);
    padding-inline: 0;
    column-gap: 0 !important;
    display: grid !important;
    grid-template-columns:
      [full-start]
      var(--full)
      [feature-max-start]
      minmax(0, var(--feature-max-width, var(--feature-max-width)))
      [feature-start]
      minmax(0, var(--feature-width, var(--feature-width)))
      [content-start]
      min(var(--content-width), 100% - var(--gutter) * 2)
      [content-end]
      minmax(0, var(--feature-width, var(--feature-width)))
      [feature-end]
      minmax(0, var(--feature-max-width, var(--feature-max-width)))
      [feature-max-end]
      var(--full)
      [full-end];

    > :not(.content--feature, .content--feature-max, .content--full, .content--full-safe) {
      max-width: 100%;
      width: 100%;
    }

    > :where(:not(.content--feature, .content--feature-max, .content--full, .content--full-safe)) {
      grid-column: content;
    }

    > .is-bg {
      grid-column: full;
    }

    &:is([class*='section--'], [class*='header--']) {
      padding-inline: 0;
    }
  }
  /* End Content Grid */

