--accent-color: #fff8dc;
  display: var(--display, none);
  flex-direction: column;
  gap: calc(var(--content-gap, 1em) / 2);
  background-color: var(--accent-color);
  color: oklch(from var(--accent-color) 0.15 c h / 1);
  border-left: 4px solid oklch(from var(--accent-color) calc(l / 1.5) calc(c * 6) h / 1);
  padding: 15px 20px;
  border-radius: 4px;
  font-family: Arial, sans-serif;
  line-height: 1.6;
  inline-size: 100%;
  max-inline-size: var(--max-width);
  margin: var(--margin-block) var(--margin-inline);
  z-index: 999;
  h1,h2,h3,h4,h5,h6,p {
    color: oklch(from var(--accent-color) 0.15 c h / 1);
  }
  &[data-display='Etch Only' i].etch-builder-block {
    --display: flex;
  }
  &[data-display='Gutenberg Only' i].wp-block {
    --display: flex;
  }
  &[data-display='Etch and Gutenberg' i]:is(.wp-block, .etch-builder-block) {
    --display: flex;
  }
  &[data-style='danger' i] {
    --accent-color: #f8d7d9;
  }
  &[data-style='info' i] {
    --accent-color: #d1edf0;
  }
  &::before {
    content: attr(data-heading);
    font-weight: bold;
    font-size: 0.8rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
  }
  pre {
    white-space: pre-wrap;
    font-size: 0.95rem;
    inline-size: 100%;
    max-inline-size: max-content;
    border-radius: 5px;
    padding: 1.5rem;
    background: oklch(from var(--black, black) l c h / 0.1);
    code {
      white-space: inherit;
      font-size: 0.9rem;
    }
  }
